const myLibrary = [];

function Book(title, author, pages, read, image) {
    this.title = title;
    this.author = author;
    this.pages = pages;
    this.read = read;
    this.image = image;
}

function addBookToLibrary(title, author, pages, read, image) {
    const newBook = new Book(title, author, pages, read, image);
    myLibrary.push(newBook);
}

addBookToLibrary('The Great Gatsby', 'F. Scott Fitzgerald', 218, 'No', 'data:image/jpeg;base64,');
addBookToLibrary('Macolm X', 'Alex Haley', 433, 'Yes', 'data:image/jpeg;base64,');
addBookToLibrary('Think and Grow Rich', 'Napolean Hill', 218, 'yes', 'data:image/jpeg;base64,');
addBookToLibrary('The Great Gatsby', 'Marry Bennet and Davis S Percy', 218, 'No', 'data:image/jpeg;base64,');

const displayContainer = document.getElementById('display-container');
const addBookBtn = document.getElementById('add-book');
const newBookInfo = document.getElementById('book-form');

Book.prototype.toggleRead = function() {
    this.read = this.read === 'Yes' ? 'No' : 'Yes';
}

function displayCard() {
    displayContainer.innerText = '';
    let divCounter = 0;
    let rowDiv;
    for (let i = 0; i < myLibrary.length; i++) {
        if (i % 3 === 0) {
            rowDiv = document.createElement('div');
            rowDiv.className = 'row';
            displayContainer.appendChild(rowDiv);
        }

        divCounter++;
        let bookInfo = myLibrary[i];
        
        let newDiv = document.createElement('div');
        newDiv.className = 'book-card';
        newDiv.setAttribute('data-div-id', divCounter);
        rowDiv.appendChild(newDiv);
        let bookImg = document.createElement('img');
        bookImg.src = bookInfo.image;
        newDiv.appendChild(bookImg);

        let bookDetails = document.createElement('div');
        bookDetails.innerHTML = `<span class='title'>Title: ${bookInfo.title}</span><br>
        <span class='author'>Author: ${bookInfo.author}</span><br>
        Pages: ${bookInfo.pages}<br><br>`;
        newDiv.appendChild(bookDetails);

        let readDiv = document.createElement('div');
        readDiv.className = 'read-div';
        readDiv.setAttribute('data-div-id', divCounter);
        newDiv.appendChild(readDiv);
        readDiv.innerHTML = ` Read: <span class='read-status'>${bookInfo.read}</span>`;

        let readStatusBtn = document.createElement('button');
        readStatusBtn.className = 'toggle-btn';
        readStatusBtn.setAttribute('data-div-id', divCounter);
        readStatusBtn.textContent = 'Change';
        readDiv.appendChild(readStatusBtn);
        
        readStatusBtn.addEventListener('click', () => {
            bookInfo.toggleRead();
            readDiv.querySelector('.read-status').textContent = bookInfo.read;
        });

        let removeBtn = document.createElement('button');
        removeBtn.className = 'remove-btn';
        removeBtn.setAttribute('data-div-id', divCounter);
        removeBtn.textContent = 'Remove';
        newDiv.appendChild(removeBtn);

        removeBtn.addEventListener('click', () => {
            newDiv.remove();
            myLibrary.splice(i, 1);
        });
    }
}

displayCard();

addBookBtn.addEventListener('click', () => {
    document.getElementById('sidebar').style.display = 'block';
});

newBookInfo.addEventListener('submit', (event) => {
    event.preventDefault();

    let title = document.getElementById('title').value;
    let author = document.getElementById('author').value;
    let pages = document.getElementById('pages').value;
    let readCheckbox = document.getElementById('read');
    let image = document.getElementById('image').value;

    let read = readCheckbox.checked ? 'Yes' : 'No';

    addBookToLibrary(title, author, pages, read, image);
    displayCard();
    newBookInfo.reset();
    document.getElementById('sidebar').style.display = 'none';
});

document.addEventListener('DOMContentLoaded', () => {
    const cancelBtn = document.getElementById('hide-btn');

    cancelBtn.addEventListener('click', () => {
        document.getElementById('sidebar').style.display = 'none';
    });
});
